// MARK: Convenience

extension Int {
    var UInt32Value: UInt32 {
        return UInt32(self)
    }
}

extension BSON.AutoReleasingCarrier {
    convenience init(document: BSON.Document) throws {
        guard let bson = BSON.unsafePointerFromDocument(document) else {
            throw MongoError.CorruptDocument
        }

        self.init(bson: bson)
    }
}
